import React, { useEffect, useState } from "react";
import axios from "axios";

const PlantInfo = ({ plant }) => {
  const sensorData = plant.sensorData || {};
  const careValues = plant.careValues || {};

  const [aiValues, setAiValues] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!plant.species) return;

    setLoading(true);
    setError(null);

    axios
      .post("http://localhost:5000/api/aiRecommendation/recommend-values", {
        species: plant.species,
      })
      .then((res) => {
        setAiValues(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch AI recommendation:", err);
        setError("ไม่สามารถโหลดค่าที่แนะนำจาก AI ได้");
        setLoading(false);
      });
  }, [plant.species]);

  // แยกค่าที่มาจาก AI
  const aiMoistureLevel = aiValues?.moistureLevel ?? null;
  const aiLightLevel = aiValues?.lightLevel ?? null;
  const aiNitrogen = aiValues?.npkNitrogen ?? null;
  const aiPhosphorus = aiValues?.npkPhosphorus ?? null;
  const aiPotassium = aiValues?.npkPotassium ?? null;

  // แยกค่าที่มาจากเซ็นเซอร์หรือ mock
  const sensorMoistureLevel = sensorData.moistureLevel ?? null;
  const sensorLightLevel = sensorData.lightLevel ?? null;
  const sensorNitrogen = sensorData.npkNitrogen ?? null;
  const sensorPhosphorus = sensorData.npkPhosphorus ?? null;
  const sensorPotassium = sensorData.npkPotassium ?? null;
  const sensorTemperature = sensorData.temperature ?? null;

  // ค่า fallback สำหรับการแสดงผลรวม
  const moistureLevel = aiMoistureLevel ?? sensorMoistureLevel ?? 50;
  const lightLevel = aiLightLevel ?? sensorLightLevel ?? 60;
  const nitrogenLevel = aiNitrogen ?? sensorNitrogen ?? 40;
  const phosphorusLevel = aiPhosphorus ?? sensorPhosphorus ?? 30;
  const potassiumLevel = aiPotassium ?? sensorPotassium ?? 20;
