              {!loading && !error && (
                <>
                  <p>ความชื้น: {aiMoistureLevel ?? "N/A"}%</p>
                  <p>แสง: {aiLightLevel ?? "N/A"}%</p>
                  <p>ไนโตรเจน (N): {aiNitrogen ?? "N/A"}%</p>
                  <p>ฟอสฟอรัส (P): {aiPhosphorus ?? "N/A"}%</p>
                  <p>โพแทสเซียม (K): {aiPotassium ?? "N/A"}%</p>
                </>
              )}
            </div>

            {/* Section: Sensor Data */}
            <div className="border rounded-lg p-4 text-sm space-y-2">
              <h3 className="font-medium mb-2">🧪 ค่าจากเซ็นเซอร์</h3>
              <p>ความชื้น: {sensorMoistureLevel ?? "N/A"}%</p>
              <p>แสง: {sensorLightLevel ?? "N/A"}%</p>
              <p>ไนโตรเจน (N): {sensorNitrogen ?? "N/A"}%</p>
              <p>ฟอสฟอรัส (P): {sensorPhosphorus ?? "N/A"}%</p>
              <p>โพแทสเซียม (K): {sensorPotassium ?? "N/A"}%</p>
              <p>อุณหภูมิ: {sensorTemperature ?? "N/A"}°C</p>
            </div>

            {/* Visual Summary (รวมค่าที่ดีที่สุดที่มี) */}
            <div className="border rounded-lg p-4">
              <h3 className="font-medium mb-2">📊 สรุปค่าที่ใช้งาน</h3>
              <div className="space-y-2 text-sm">
                <div>
                  <div className="flex justify-between">
                    <span>ความชื้น</span>
                    <span>{moistureLevel.toFixed(1)}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div className="bg-blue-600 h-2 rounded-full" style={{ width: `${moistureLevel}%` }}></div>
                  </div>
                </div>
                <div>
                  <div className="flex justify-between">
                    <span>แสง</span>
                    <span>{lightLevel.toFixed(1)}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div className="bg-yellow-400 h-2 rounded-full" style={{ width: `${lightLevel}%` }}></div>
                  </div>
                </div>
                <div>
                  <div className="flex justify-between">
                    <span>ไนโตรเจน (N)</span>
                    <span>{nitrogenLevel.toFixed(1)}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div className="bg-green-500 h-2 rounded-full" style={{ width: `${nitrogenLevel}%` }}></div>
                  </div>
                </div>
                <div>
                  <div className="flex justify-between">
                    <span>ฟอสฟอรัส (P)</span>
                    <span>{phosphorusLevel.toFixed(1)}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div className="bg-green-500 h-2 rounded-full" style={{ width: `${phosphorusLevel}%` }}></div>
                  </div>
                </div>
                <div>
                  <div className="flex justify-between">
                    <span>โพแทสเซียม (K)</span>
