import { useEffect, useState } from "react";

const PlantInfo = ({ plant }) => {
  const sensorData = plant.sensorData || {};
  const [recommended, setRecommended] = useState(null);

  const moistureLevel = sensorData.moistureLevel ?? 50;
  const lightLevel = sensorData.lightLevel ?? 60;
  const nitrogenLevel = sensorData.npkNitrogen ?? 40;
  const phosphorusLevel = sensorData.npkPhosphorus ?? 30;
  const potassiumLevel = sensorData.npkPotassium ?? 20;

  useEffect(() => {
    const fetchAIRecommendation = async () => {
      try {
        const response = await fetch("/api/openai/recommend-values", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ species: plant.species || plant.name }),
        });

        const data = await response.json();
        setRecommended(data);
      } catch (error) {
        console.error("Error fetching AI recommendation:", error);
      }
    };

    fetchAIRecommendation();
  }, [plant.species, plant.name]);

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex flex-col md:flex-row gap-6">
        <div className="md:w-1/3">
          <div className="aspect-square rounded-lg overflow-hidden">
            <img
              src={`http://localhost:5000${plant.imageUrl}`}
              alt={plant.name}
              className="w-full h-full object-cover"
            />
          </div>
        </div>

        <div className="md:w-2/3">
          <h1 className="text-2xl font-bold mb-2">{plant.name}</h1>
          {plant.species && <p className="text-gray-500 mb-4">{plant.species}</p>}

          {/* แสดงค่าจากเซนเซอร์และค่าที่แนะนำจาก AI side-by-side */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            {/* ค่าจากเซนเซอร์ */}
            <div className="border rounded-lg p-4">
              <h3 className="font-medium mb-2">ค่าจากเซนเซอร์</h3>
              <Progress label="🌱 ความชื้นในดิน" value={moistureLevel} />
              <Progress label="🌞 แสง" value={lightLevel} />
              <Progress label="🧪 ไนโตรเจน (N)" value={nitrogenLevel} />
              <Progress label="🧪 ฟอสฟอรัส (P)" value={phosphorusLevel} />
              <Progress label="🧪 โพแทสเซียม (K)" value={potassiumLevel} />
            </div>

            {/* ค่าที่แนะนำจาก AI */}
            {recommended && (
              <div className="border rounded-lg p-4">
                <h3 className="font-medium mb-2">ค่าที่แนะนำจาก AI</h3>
                <Progress label="🌱 ความชื้นในดิน" value={recommended.moistureLevel ?? 0} />
                <Progress label="🌞 แสง" value={recommended.lightLevel ?? 0} />
                <Progress label="🧪 ไนโตรเจน (N)" value={recommended.npkNitrogen ?? 0} />
                <Progress label="🧪 ฟอสฟอรัส (P)" value={recommended.npkPhosphorus ?? 0} />
                <Progress label="🧪 โพแทสเซียม (K)" value={recommended.npkPotassium ?? 0} />
              </div>
            )}
          </div>

          {/* คำแนะนำการดูแล */}
          {plant.careRecommendations && (
            <div className="border rounded-lg p-4">
              <h3 className="font-medium mb-2">คำแนะนำในการดูแล</h3>
              <p className="text-sm whitespace-pre-line">{plant.careRecommendations}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const Progress = ({ label, value }) => (
  <div>
    <div className="flex justify-between mb-1">
      <span>{label}</span>
      <span>{value?.toFixed(1)}%</span>
    </div>
    <div className="w-full bg-gray-200 rounded-full h-1.5">
      <div
        className="bg-green-500 h-1.5 rounded-full"
        style={{ width: `${value}%` }}
      ></div>
    </div>
  </div>
);

export default PlantInfo;
